    console.log("   (Assuming DNS resolves and TCP/TLS handshakes complete successfully)");

    try {
        console.log(`3. Browser constructs and sends HTTP/HTTPS request to: ${url}`);
        const response: Response = await fetch(url, {
            method: 'GET',
            headers: {
                'User-Agent': 'MyCustomBrowser/1.0',
                'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9',
            },
            // Note: 'credentials: "include"' would send cookies
        });

        console.log(`4. Server responded with status: ${response.status} ${response.statusText}`);
        if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const contentType = response.headers.get('Content-Type');
        console.log(`   Content-Type: ${contentType}`);

        // Simulate browser processing based on Content-Type
        if (contentType && contentType.includes('text/html')) {
            const htmlContent: string = await response.text();
            console.log("5. Browser receives HTML content.");
            console.log("   (Browser starts parsing HTML, building DOM, fetching CSS/JS, rendering)");
            // In a real browser, this would be the rendering engine's job
            // console.log("--- Received HTML (first 200 chars): ---");
            // console.log(htmlContent.substring(0, 200) + "...");
            // console.log("------------------------------------------");
            return htmlContent;
        } else if (contentType && contentType.includes('application/json')) {
            const jsonContent: any = await response.json();
            console.log("5. Browser receives JSON content.");
            // console.log("--- Received JSON: ---");
            // console.log(JSON.stringify(jsonContent, null, 2));
            // console.log("----------------------");
            return JSON.stringify(jsonContent);
        } else {
            console.log("5. Browser received other content type, handling accordingly.");
            return `Received content of type: ${contentType}`;
        }

    } catch (error) {
        console.error("An error occurred during fetch:", error);
        return `Error: ${error.message}`;
    }
}

// Example usage:
