        }

        .float-right {
            float: right;
        }

        .content {
            background-color: #f0f0f0;
            padding: 10px;
            border: 1px solid #eee;
        }

        /* Element that clears the float */
        .clearing-element {
            background-color: lightcoral;
            color: white;
            padding: 10px;
            margin-top: 20px;
            font-weight: bold;
            /* This is where clear comes into play */
            clear: both; /* Ensures this element goes below all preceding floats */
        }

        /* Another way to clear floats is using a clearfix hack on the parent */
        .clearfix::after {
            content: "";
            display: table;
            clear: both;
        }
    </style>
