        owner: "Bob",
        expectedCompletion: new Date('2023-10-28'),
        status: 'Blocked',
        blockerDetails: 'Waiting for external API keys'
    }
];

// Example function to update status
function updateDependency(featureName: string, newStatus: DependencyStatus['status'], details?: string) {
    const dependency = integrationDependencies.find(dep => dep.featureName === featureName);
