import App from './src/App'; // Your main React component

const app = express();
const port = 3000;

app.get('/', (req, res) => {
  // 1. Render the React component to an HTML string
  const appHtml = ReactDOMServer.renderToString(<App />);

  // 2. Wrap it in a full HTML document
  const html = `
    <!DOCTYPE html>
    <html lang="en">
    <head>
      <meta charset="UTF-8">
      <meta name="viewport" content="width=device-width, initial-scale=1.0">
      <title>SSR Example</title>
    </head>
    <body>
      <div id="root">${appHtml}</div>
      <!-- 3. Client-side JavaScript bundle will hydrate this later -->
      <script src="/static/bundle.js"></script> 
    </body>
    </html>
  `;

  // 4. Send the complete HTML to the client
  res.send(html);
});

// Serve static assets (like the client-side JS bundle)
app.use('/static', express.static('public'));

app.listen(port, () => {
  console.log(`Server running on http://localhost:${port}`);
});

// src/App.tsx (Your React component)
