function App() {
  const [count, setCount] = React.useState(0); // State will be hydrated client-side

  return (
    <div>
      <h1>Hello from SSR!</h1>
      <p>This content was rendered on the server.</p>
      <p>Count: {count}</p>
      <button onClick={() => setCount(count + 1)}>Increment</button>
    </div>
  );
}

export default App;

// public/bundle.js (Simplified client-side hydration - typically generated by a bundler like Webpack/Vite)
