const calculateButton = document.getElementById('calculate');

calculateButton?.addEventListener('click', () => {
    resultElement!.textContent = 'Calculating...';

    // Create a new Web Worker
    const worker = new Worker('worker.js');

    // Send data to the worker
    worker.postMessage(1000000000); // Calculate sum up to 1 billion

    // Listen for messages from the worker
    worker.onmessage = (event) => {
        resultElement!.textContent = `Calculation result: ${event.data}`;
        worker.terminate(); // Terminate the worker once done
    };

    // Handle errors from the worker
    worker.onerror = (error) => {
        resultElement!.textContent = `Worker error: ${error.message}`;
        console.error('Worker error:', error);
    };
});
```

